import React, { useState } from "react";
import PokemonCard from "./PokemonCard";
import { Link } from "react-router-dom";

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [favourite, setFavourite] = useState([]);

  async function handleFavourite() {
    try {
      const response = await fetch('http://127.0.0.1:5000/favourite', {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${sessionStorage.getItem('access_token')}`,
        },
      });
      const data = await response.json();
      setFavourite(Object.values(data))
    } catch(err) {
      console.log("ERROR: ", err);
    }
  }

  const toggleSidebar = () => {
    if(sessionStorage.getItem('access_token')) {
      setIsOpen(!isOpen);
      handleFavourite();
    }
    else {
      alert("Please login to see your favourite pokemons");
    }
  };

  console.log(favourite);

  const pokemonEl = favourite?.map((pokemon, index) => {
    return (
      <Link key={index} to={`/details/${pokemon.name}`}>
        <PokemonCard pokemon={pokemon} index={index}>
        </PokemonCard>
      </Link>
    );
  })

  return (
    <div>
        <button onClick={toggleSidebar} className="toggle-button">
            {isOpen ? "Close Sidebar" : "Open Sidebar"}
        </button>

        <div className={`sidebar ${isOpen ? "open" : ""}`}>
            <h2>Sidebar Content</h2>
            <p>This is a simple sidebar that pops up!</p>
            <span className="close-sidebar" onClick={toggleSidebar}>X</span>
            <div className='pokemon-poster-list'>
                {pokemonEl}
            </div>
        </div>
        {isOpen && <div className="overlay"></div>}
    </div>
  );
};

export default Sidebar;
