import { useEffect, useState } from 'react';

function Home() {
  const [data, setData] = useState([]);

  useEffect(() => {
    // Fetch data from Flask API using fetch
    fetch('http://127.0.0.1:5000/api/pokemons')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(rec_data => setData(Object.values(rec_data))) // convert object to array and store in data state
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  const pokemonEl = data?.map((pokemon, index) => {
    return (
      <div key={index}>
        <h2>{pokemon.name}</h2>
        <p>{pokemon.description}</p>
        <img src={pokemon.image} alt={pokemon.name} />
      </div>
    );
  })

    return (
      <div className='pokemon-poster'> 
        <h1>Home Page</h1>
        <p>Welcome to the Home page!</p>
        {pokemonEl}
      </div>
    );
  }
  
  export default Home;
