import { useEffect, useState } from 'react';
import {Link} from 'react-router-dom';
import titleImg from '../images/pokemon_logo.png';

function Home() {
  const [data, setData] = useState([]);

  useEffect(() => {
    // Fetch data from Flask API using fetch
    fetch('http://127.0.0.1:5000/api/pokemons')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(rec_data => setData(Object.values(rec_data))) // convert object to array and store in data state
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  const pokemonEl = data?.map((pokemon, index) => {
    return (
      <Link key={index} to={`/details/${pokemon.name}`}>
        <div key={index} className='pokemon-poster normal-type' >
          <div> 
            <h3 id='pokemon-name'>{pokemon.name[0].toUpperCase() + pokemon.name.slice(1)}</h3>
            <h3 id='hp'>HP: {pokemon.hp}</h3>
          </div>
          <img src={pokemon.image} alt={pokemon.name} className='pokemon-poster-img'/>
          <div className='stats-container'>
            <div className='pokemon-stats'>
              <p><strong>ATT</strong>&nbsp;&nbsp;{pokemon.attack}</p> 
              <p><strong>DEF</strong>&nbsp;&nbsp;{pokemon.defense}</p>
            </div>
            <div className='pokemon-stats'>
              <p><strong>WEIGHT</strong>&nbsp;&nbsp;{pokemon.weight}</p>
              <p><strong>SPEED</strong>&nbsp;&nbsp;{pokemon.speed}</p>
            </div>
          </div>        
        </div>
      </Link>
    );
  })

    return (
      <>
      <img src={titleImg} alt='title' className='title-img'/>
      <div className='container'>
        <div className='pokemon-poster-list'>
            {pokemonEl}
        </div>  
      </div>

      </>
    
    );
  }
  
  export default Home;
