import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function Details() {
  const [data, setData] = useState([]);
  const pokemon_name  = useParams().name;
  console.log(pokemon_name)
  useEffect(() => {
    // Fetch data from Flask API using fetch
    fetch(`http://127.0.0.1:5000/api/pokemon/${pokemon_name}`)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(rec_data => setData(Object.values(rec_data))) // convert object to array and store in data state
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  console.log(data);
  
  return (
      <div>
        <h1>Details Page</h1>
        <p>This is the Details page.</p>
      </div>
    );
  }
  
  export default Details;
