import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import titleImg from '../images/pokemon_logo.png';
import PokemonCard from '../components/PokemonCard';

function Details() {
  const [data, setData] = useState([]);
  const pokemon_name  = useParams().name;

  useEffect(() => {
    // Fetch data from Flask API using fetch
    fetch(`http://127.0.0.1:5000/api/pokemon/${pokemon_name}`)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(rec_data => setData(Object.values(rec_data))) // convert object to array and store in data state
      .catch(error => console.error('Error fetching data:', error));
  }, [pokemon_name]);

  console.log(data);

  const pokemonEl = data?.map((pokemon, index) => {
    return (
      <PokemonCard pokemon={pokemon} index={index}>
      </PokemonCard>
    );
  })
  
  return (
    <>

      <img src={titleImg} alt='title' className='title-img'/>
      <div className='main-guy'>
      </div>
      <div className='pokemon-poster-list'>
        {pokemonEl}
      </div>
      

    </>
  )

  }
  
  export default Details;
